templates.template:
${:import(yuis.core.ns.yuis_handler)}/**
 * 
 * @param event イベント
 */
yuis_handler function ${componentId:var(flash.events.EventDispatcher)}_${eventType}(event:${eventClass}):void {
	${cursor}
}

${:import(yuis.service.event.FaultStatus,yuis.service.ns.service,yuis.service.ns.yuis_fault,yuis.service.ns.yuis_result)}

/**
 * 
 * @param ${argName} 
 */
public function ${serviceName:var(yuis.service.IService)}$$${serviceMethodName}(${argName}:${argsType}):void{
    ${serviceName}.${serviceMethodName}(${argName}).setResponder( this );
}

/**
 *
 * @param ${resultName} 
 */
yuis_result function ${serviceName}_${serviceMethodName}(${resultName}:${resultType}):void{
    ${cursor}
}

/**
 *
 * @param fault サービス呼び出しエラー
 */        
yuis_fault function ${serviceName}_${serviceMethodName}(fault:FaultStatus):void{
    trace("[FAULT:${serviceName}.${serviceMethodName}]"+fault);
}

${:import(yuis.core.ns.yuis_handler,yuis.command.events.CommandEvent)}/**
 * 
 * @param event コマンドの実行結果
 */
yuis_handler function ${cmdName:var(org.seasar.akabana.yui.command.core.ICommand)}_complete(event:CommandEvent):void{
    ${cursor}
}

/**
 * 
 * @param event コマンドの実行エラー
 */
yuis_handler function ${cmdName}_error(event:CommandEvent):void{
    trace("[ERROR:${cmdName}]"+event);
}
